class Factorial {

		public static void main(String[] args) {
			// TODO Auto-generated method stub
	        int sum = 0;
	        int fact = 1;
	        for(int i = 1; i<=20; i++) {
	        	sum += i;
	        	fact *=i;
	        	
	        	}
	        System.out.println("Sum is" +  sum);
	        System.out.println("Factorial is" + fact);
		}

	}

